mapper.select:
SELECT userid, bbsid, bbscategory, bbsdog, bbstitle, bbscontent, bbsdate, bbsavailable
FROM LBOARD
WHERE bbsid=#{bbsid}

mapper.update:
	UPDATE LBOARD
	SET bbscategory=#{bbscategory}, 
		bbstitle=#{bbstitle},
    	bbscontent=#{bbscontent},
   		bbsdate=#{bbsdate}
	WHERE bbsid=#{bbsid}
